import React , {useState} from 'react';
import './App.css';
import ImageGenerator from './components/ImageGenerator';
import ChatComponent from './components/ChatComponent';
import RecipeGenerator from './components/RecipeGenerator';

function App() {
  const [activeTab , setActiveTab] = useState("image-generator")

  const handleTabChange = (tab) => {
    // alert(tab)
    setActiveTab(tab)
  }
  return (
    <div className="App">
      <button className= {activeTab === 'image-generator' ? 'active' : ''}
       onClick={() => handleTabChange('image-generator')}>
        Image Generator
        </button>
      <button className= {activeTab === 'chat' ? 'active' : ''}
      onClick={() => handleTabChange('chat')}>
        Chat
        </button>
      <button className= {activeTab === 'recipe-generator' ? 'active' : ''}
       onClick={() => handleTabChange('recipe-generator')}>
        Recipe Generator
        </button>

        <div>
          {activeTab === 'image-generator' && <ImageGenerator></ImageGenerator>}
          {activeTab === 'chat' && <ChatComponent></ChatComponent>}
          {activeTab === 'recipe-generator' && <RecipeGenerator></RecipeGenerator>}
        </div>
    </div>
  );
}

export default App;
